import React, { Component } from 'react'
import { View, Text } from 'react-native'
import { Input } from '../input'
import { hp, wp } from './general_style'

export default class reset_password extends Component {

     constructor(props){
          super(props)

          this.state = ({

               email:''

          })
     }

     render(){

          return(

               <View style={{justifyContent:'center', alignItems:'center'}}>

                    <Text style={{color:'#6125E4', fontSize:wp(1)+hp(1), marginBottom:hp(1)}}> Ingresa el email a recuperar la contraseña </Text>
                    <Input secure={false} function_passed={(text) => this.setState({email:text})} placeholder_title={'Correo electrónico'} color_back={'white'} />
                    <Text style={{color:'black', fontSize:wp(1)+hp(1), marginTop:hp(5)}}>  Recuerda revisar la carpeta SPAM. </Text>

               </View>

          )

     }

}
